// 334. Increasing Triplet Subsequence
// https://leetcode.com/problems/increasing-triplet-subsequence/

import Cocoa

func increasingTriplet(_ nums: [Int]) -> Bool {
    var small = Int.max
    var big = Int.max
    
    for num in nums {
        if num <= small {
            small = num
        }
        else if num <= big {
            big = num
        }
        else if num > big {
            return true
        }
    }
    return false
}
func increasingTripletConsecutiveOnly(_ nums: [Int]) -> Bool {
    if nums.count < 3 {
        return false
    }
    for i in 0..<(nums.count - 3) {
        if nums[i] < nums[i + 1] && nums[i + 1] < nums[i + 2] {
            return true
        }
    }
    return false
}
func increasingTripletDoesntWorkOnNegatives(_ nums: [Int]) -> Bool {
    if nums.count < 3 {
        return false
    }
    var minimum = (Int.max, 0)
    var maximum = (Int.min, 0)
    for i in 0..<nums.count {
        if nums[i] < minimum.0 {
            minimum = (nums[i], i)
        }
        if nums[i] >= maximum.0 {
            maximum = (nums[i], i)
        }
    }
    return maximum.1 - minimum.1 > 1 && maximum.0 - minimum.0 >= 2
}

