// 1044. Longest Duplicate Substring (Hard)
// https://leetcode.com/problems/longest-duplicate-substring/

// My naive solution was taking too long, so I had to borrow another poster's solution
// https://leetcode.com/problems/longest-duplicate-substring/discuss/2467829/Swift-or-Binary-Search-%2B-Rabin-Karp-or-Faster-than-100

public class Problem1044 {
    public func run() {
        let solve1 = longestDupSubstring("banana")
        print("\'\(solve1)' -- should be 'ana'")

        let solve2 = longestDupSubstring("abcd")
        print("'\(solve2)' -- should be empty string")

        let solve3 = longestDupSubstring("")
        print("'\(solve3)' -- should be ''")

        let solve4 = longestDupSubstring("nyvzwttxsshphczjjklqniaztccdrawueylaelkqtjtxdvutsewhghcmoxlvqjktgawwgpytuvoepnyfbdywpmmfukoslqvdrkuokxcexwugogcwvsuhcziwuwzfktjlhbiuhkxcreqrdbj")
        print("\'\(solve4)' -- should be 'hcz'")

        let solve5 = longestDupSubstring("")
        print("\'\(solve5)' -- should be 'njky'")

        let solve6 = longestDupSubstring("")
        print("\'\(solve6)' -- should be 'maoli'")
    }

    public func test(_ s: String) -> String {
        return longestDupSubstring(s)
    }

    func longestDupSubstring(_ s: String) -> String {
        let s: [Character] = Array(s)
        var low = 0
        var high = s.count - 1
        let mod = Int(10e9 + 7)

        func rollHash(_ n: Int) -> ArraySlice<Character> {
            var hash = 0
            var dict: [Int: ArraySlice<Character>] = [:]
            for i in 0 ..< n {
                let ascii = Int(s[i].asciiValue!)
                hash = (hash * 31 + ascii) % mod
            }

            dict[hash] = s[0..<n]
            var power = 1
            for _ in 0..<(n - 1) {
                power = power * 31 % mod
            }

            var firstIndex = 0
            for i in n..<s.count {
                let first = Int(s[firstIndex].asciiValue!)
                let current = Int(s[i].asciiValue!)
                hash = (hash - power * first) % mod
                if hash < 0 {
                    hash += mod
                }
                hash = (hash * 31 + current) % mod

                if let val = dict[hash], val == s[firstIndex + 1 ... i] {
                    return s[firstIndex + 1 ... i]
                }

                dict[hash] = s[firstIndex + 1 ... i]
                firstIndex += 1
            }

            return []
        }

        var result: ArraySlice<Character> = []
        while low <= high {
            let mid = low + (high - low) / 2
            let slice = rollHash(mid + 1)
            if slice.count > result.count {
                result = slice
                low = mid + 1
            } else {
                high = mid - 1
            }
        }

        return String(result)
    }

    func checkLength(_ s: String, _ len: Int) -> String {
        let stringLength = s.count
        var stringSet = Set<String>()
        for idx in 0...stringLength - len {
            let start = s.index(s.startIndex, offsetBy: idx)
            let end = s.index(s.startIndex, offsetBy: idx + len)
            let range = start..<end
            let str = String(s[range])
            if stringSet.contains(str) {
                return str
            } else {
                stringSet.insert(str)
            }
        }

        return ""
    }

    func longestDupSubstring_naive(_ s: String) -> String {
        let stringLength = s.count
        var longestString = ""
        var lastFoundLength = 0
        var lastNotFoundLength = stringLength
        var len = stringLength / 2
        while true {
            let str = checkLength(s, len)
            if str.isEmpty {
                lastNotFoundLength = len
                let newLen = (len + lastFoundLength) / 2
                if len == newLen {
                    return longestString
                } else {
                    len = newLen
                }
            } else {
                if str.count > longestString.count {
                    longestString = str
                }

                lastFoundLength = len
                let newLen = (len + lastNotFoundLength) / 2
                if len == newLen {
                    return longestString
                } else {
                    len = newLen
                }
            }
        }
    }
}
