    func test_Problem1044() throws {
        let solve1 = Problem1044().test("banana")
        XCTAssertEqual(solve1, "ana")

        let solve2 = Problem1044().test("abcd")
        XCTAssertEqual(solve2, "")

        let solve3 = Problem1044().test("")
        XCTAssertEqual(solve3, "")

        let solve4 = Problem1044().test("nyvzwttxsshphczjjklqniaztccdrawueylaelkqtjtxdvutsewhghcmoxlvqjktgawwgpytuvoepnyfbdywpmmfukoslqvdrkuokxcexwugogcwvsuhcziwuwzfktjlhbiuhkxcreqrdbj")
        XCTAssertEqual(solve4, "hcz")

        let solve5 = Problem1044().test("")
        XCTAssertEqual(solve5, "njky")

        let solve6 = Problem1044().test("")
        XCTAssertEqual(solve6, "maoli")
    }

    func test_Problem1045() throws {
